        private void RegisterLabel_Clicked()
        {
            //not an autogenerated file
            RegisterLabel.GestureRecognizers.Add(new TapGestureRecognizer()
            {
                Command = new Command(() =>
                {
                    Navigation.PushAsync(new RegisterPage());
                }
                )
            });

        }

